import { Add, Remove } from '@mui/icons-material';
import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import styled from 'styled-components';
import Announcement from '../components/announcements';
import Footer from '../components/footer';
import Navbar from '../components/navbar';
import { publicrequest } from '../requestmethod';

const Container =styled.div`

`
const Imagecontainer =styled.div`
    flex:1;

`
const Infocontainer =styled.div`
    flex:1;
    margin-right:70px;
`
const Wrapper =styled.div`
    display:flex;


`
const Heading =styled.h1`
    font-size:60px;
`
const Description=styled.p`
    text-align: justify;
`
const Price =styled.div`
    font-size: 40px;
    font-weight: 50;
`
const Image =styled.img`

`

const Filtercontainer=styled.div`
    display:flex;
    margin-top:20px;

`
const Filter=styled.div`
    display:flex;
    justify-content: center;
    margin:10px;
`
const Filtertitle=styled.div `
    font-size: 20px;
`

const Filtersize=styled.select`

`
const Filtersizeoption=styled.option`

`
const Filtercolour=styled.div`
    width:20px;
    height:20px;
    background-color: ${props=>props.color};
    margin:0px 5px;
    border-radius:50%;
    cursor: pointer;
`

const Addcontainer=styled.div`
    display:flex;
    align-items: center;
    font-weight:700;
`
const Amount =styled.span`
    width:30px;
    height:30px;
    border-radius: 10px;
    border:1px solid maroon;
    display: flex;
    justify-content: center;
    align-items: center;
    margin:0px,5px;
`
const Button=styled.button`
    padding:10px;
    border:2px solid maroon;
    background-color: white;
    cursor:pointer;
    margin-left:21px;
    border-radius: 5px;
    &:hover{    
        background-color: bisque;

    }
`
const Amountcontainer=styled.div`
    display:flex;
    width:50%;
    align-items: center;


`


function Singleproduct(){
    // const location=useLocation();
    // const id=(location.pathname.split('/')[2]);

    // const[product,setproduct]=useState({});

    // useEffect(()=>{
    //     const getproducts=async()=>{
    //         try{
    //             const res=await axios.get('http://localhost:5000/product/api/find/'+id);
    //             setproduct(res.data);

    //         }
    //          catch(err){}
    //     }
    //     getproducts();
    // },[id])

    return (
        <Container >
            <Announcement />
            <Navbar />
            <Wrapper>
                <Imagecontainer>
                    <Image />
                </Imagecontainer>
                <Infocontainer >
                    <Heading>
                        Jeans
                    </Heading>
                    <Description>
                        Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ducimus voluptates ipsa eius perferendis molestias tempore quod iusto repellendus. Aut reprehenderit laudantium natus esse accusantium recusandae dolorem minus, doloribus est quia.
                        Tenetur eius modi, nesciunt totam adipisci quod. Voluptatum dolor quis nemo et delectus suscipit saepe corrupti quaerat minus aspernatur numquam atque, dolores fugiat dignissimos fuga! Consectetur similique laboriosam at expedita!
                    </Description>
                    <Price>
                    ₹2400
                    </Price>
                    <Filtercontainer >
                        <Filter>
                        <Filtertitle>Colour</Filtertitle>
                        <Filtercolour color='black' />
                        <Filtercolour color='darkblue' />
                        <Filtercolour color='grey' />
                        </Filter>
                        <Filter>
                            <Filtertitle>Size</Filtertitle>
                            <Filtersize>
                                <Filtersizeoption>XS</Filtersizeoption>
                                <Filtersizeoption>S</Filtersizeoption>
                                <Filtersizeoption>M</Filtersizeoption>
                                <Filtersizeoption>L</Filtersizeoption>
                                <Filtersizeoption>XL</Filtersizeoption>
                            </Filtersize>
                        </Filter> 

                    </Filtercontainer>
                    <Amountcontainer>
                    <Addcontainer>
                        <Remove/>
                        <Amount>1</Amount>
                        <Add/>
                    </Addcontainer>
                    <Button>ADD TO CART</Button>
                </Amountcontainer>   

                </Infocontainer>


            </Wrapper>

            <Footer />
        </Container>
    )
}

export default Singleproduct;
