import React from 'react';
import styled from 'styled-components';
import Announcement from '../components/announcements';
import Footer from '../components/footer';
import Navbar from '../components/navbar';

const Container=styled.div`

`
const Wrapper=styled.div`

`
const Title=styled.h1`
    text-align: center;
`
const Top=styled.div`
    display:flex;
    justify-content: space-between;
    align-items: center;
`
const Bottom=styled.div`
    display:flex;


`
const TopButton=styled.button`
    width:130px;
    height:40px;
    margin:20px;
    border:${props=>props.type==='filled' && 'none'};
    background-color: ${props=>props.type==='filled'?'black':'transparent'};
    color:${props=>props.type==='filled' && 'white'};
    cursor:pointer;
`
const Toptext=styled.span`
    text-decoration: underline;
    cursor: pointer;
`
const Info=styled.div`
    flex:1;
    flex-direction: row;
`
const Summary=styled.div`
    flex:1;
`
const Productdetail=styled.div`

    margin: 20px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
`
const Image=styled.img`
    height:15%;
    width:16%;
`
const Productname=styled.div`

`
const Productid=styled.div`

`
const Productcolor=styled.div`

`
const Productsize=styled.div`

`
const Pricedetail=styled.div`

`


function Cart(){
    return(
        <Container >
            <Announcement />
            <Navbar/>
            <Wrapper>
                <Title>Your Cart</Title>
                <Top>
                    <TopButton>Continue Shopping</TopButton>
                    <Toptext>Shopping bag(2)</Toptext>
                    <Toptext>Your Cart(0)</Toptext> 
                    <TopButton type='filled'>Checkout</TopButton>
                </Top>
                <Bottom>
                    <Info>
                    <Productdetail>
                            <Image src='https://rukminim1.flixcart.com/image/832/832/xif0q/shoe/y/h/m/7-dj7381-400-8-nike-ashen-slate-black-white-football-grey-original-imagmnc8yhsrndbf.jpeg?q=70'/>
                            <Productname>
                                <b>Product:</b>NIKE RUNNING
                            </Productname>
                            <Productid><b>ID:</b>NK938738</Productid>
                            <Productcolor/>
                            <Productsize><b>SIZE:</b>Uk10</Productsize>
                        </Productdetail>
                        <Pricedetail>
                            price
                        </Pricedetail>
                    </Info>
                    <Summary>

                    </Summary>

                </Bottom>

            </Wrapper>

            <Footer />

        </Container>
    )
}
export default Cart;
