import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { popularproduct } from '../items';
import Product from './product';
import axios from 'axios';

const Container = styled.div`
  display:flex; 
  padding:30px;
  flex-wrap: wrap;
  justify-content: space-between;
`







function Products({cat,sort,filter }){

    const[products,setproducts]=useState([]);
    const[filteredproducts,setfilteredproducts]=useState([]);

    useEffect(()=>{
         const getproduct=async()=>{
            try{
                const response=await axios.get(cat? `http://localhost:5000/api/product?catgory=${cat}`:"http://localhost:5000/api/product");
                setproducts(response.data);
            }catch(err){

            }
         }
        getproduct(); 
    },[cat])


     useEffect(()=>{
        cat && setfilteredproducts(
            products.filter(item=> Object.entries(filter).every(([key,value])=>{
                item[key].includes(value) 
            }))
        ) 
     },[cat,products,filter])

    console.log(cat,sort,filter);
    return (
        <Container>
            {filteredproducts.map((item) => {
                <Product item={item} key={item.id}/>
            }
            )}

        </Container>
    )



}

export default Products;
